mapper.select:

    	select rnum, ARTIST_NUM,  ARTIST_NAME, ARTIST_IMG , FOLLOWER, I.ARTINFO_NUM, I.ARTINFO_IMG  from 
		(
		select rnum, ARTIST_NUM,  ARTIST_NAME, ARTIST_IMG , FOLLOWER from ( select rownum rnum, G.* from
		(
		    SELECT A.ARTIST_NUM,  A.ARTIST_NAME, A.ARTIST_IMG , nvl(F.FOLLOWER, 0) FOLLOWER
		    FROM ARTIST A LEFT OUTER JOIN (select ARTIST_NUM, COUNT(ARTIST_NUM) FOLLOWER from ARTIST_FOLLOWER group by ARTIST_NUM) F ON A.ARTIST_NUM=F.ARTIST_NUM
		    ORDER BY FOLLOWER DESC, ARTIST_NUM ASC
		) G ) H where rnum>=#{startRnum} and rnum<=#{endRnum}
		) J LEFT OUTER JOIN ART_INFO I using (ARTIST_NUM)
		order by artist_num

   		SELECT COUNT(FOLLOWER_NUM)
		FROM ARTIST_FOLLOWER
		WHERE ID=#{userId}

SELECT COUNT(ART_SCRAP_NUM)
FROM ART_SCRAP
WHERE ID=#{userId}

SELECT MEMBER_IMG
FROM MEMBER
WHERE ID=#{userId}

SELECT COUNT (ARTINFO_NUM)
FROM ART_INFO
WHERE ARTIST_NUM=#{artistNum}

SELECT COUNT (FOLLOWER_NUM)
FROM ARTIST_FOLLOWER
WHERE ARTIST_NUM=#{artistNum}

SELECT COUNT(DISTINCT id)
FROM ARTIST_CONTRIBUTOR
WHERE ARTIST_NUM=#{artistNum}

SELECT count(*)
FROM ARTIST_FOLLOWER
WHERE ARTIST_NUM=#{artistNum} AND ID=#{userId}
